import React, { useState } from "react";
import { InputGroup, InputGroupText, InputGroupAddon, Input } from 'reactstrap';

const Popup = (props) => {

    //const [isLost, setLost] = useState("lost")


    return (
        <div className="popup-box">
            <div className="box">
                <span className="close-icon" onClick={props.handleClose}>x</span>
                <h1>{props.itemState}</h1>
                <div class="inputHolder">
                    <input type="text" placeholder="Title" className="postInput"></input> <br></br>
                    <label for="time">{props.timeLabel}:</label>
                        <input type="time" id="time" name="time"></input> <br></br>
                    <label for="itemDate">{props.dateLabel}:</label>
                        <input type="date" id="itemDate" name="itemDate"></input> <br></br>

                    <button type="button" id="addPost">Post</button>
                 </div>
            </div>
        </div>
    );
}

export default Popup;
